using System;using System.Collections.Generic;using System.Text;using MyWeb.Data;namespace MyWeb.Business{	public class ConfigService	{		private static ConfigDAL db = new ConfigDAL();		#region[Config_GetById]		public static List<Data.Config> Config_GetById(string Id)		{			return db.Config_GetById(Id);		}		#endregion		#region[Config_GetByTop]		public static List<Data.Config> Config_GetByTop(string Top, string Where, string Order)		{			return db.Config_GetByTop(Top, Where, Order);		}		#endregion		#region[Config_GetByAll]		public static List<Data.Config> Config_GetByAll()		{			return db.Config_GetByAll();		}		#endregion		#region[Config_Paging]		public static List<Data.Config> Config_Paging(string CurentPage, string PageSize)		{			return db.Config_Paging(CurentPage, PageSize);		}		#endregion		#region[Config_Insert]		public static bool Config_Insert(Config data)		{			return db.Config_Insert(data);		}		#endregion		#region[Config_Update]		public static bool Config_Update(Config data)		{			return db.Config_Update(data);		}		#endregion		#region[Config_Delete]		public static bool Config_Delete(string Id)		{			return db.Config_Delete(Id);		}		#endregion	}}