using System;using System.Data;using System.Data.SqlClient;using System.Collections.Generic;namespace MyWeb.Data{	public class Branch	{		#region[Declare variables]		private string  _Id;		private string  _Name;		private string  _Ord;		private string  _Logo;		#endregion		#region[Public Properties]		public string Id{ get { return _Id; } set { _Id = value; } }		public string Name{ get { return _Name; } set { _Name = value; } }		public string Ord{ get { return _Ord; } set { _Ord = value; } }		public string Logo{ get { return _Logo; } set { _Logo = value; } }		#endregion		#region[Branch IDataReader]		public Branch BranchIDataReader(IDataReader dr)		{			Data.Branch obj = new Data.Branch();			obj.Id = (dr["Id"] is DBNull) ? string.Empty : dr["Id"].ToString();			obj.Name = (dr["Name"] is DBNull) ? string.Empty : dr["Name"].ToString();			obj.Ord = (dr["Ord"] is DBNull) ? string.Empty : dr["Ord"].ToString();			obj.Logo = (dr["Logo"] is DBNull) ? string.Empty : dr["Logo"].ToString();			return obj;		}		#endregion	}}