using System;using System.Data;using System.Data.SqlClient;using System.Collections.Generic;namespace MyWeb.Data{	public class NewsCategory	{		#region[Declare variables]		private string  _Id;		private string  _Name;		private string  _Ord;		private string  _Active;		private string  _Tag;		private string  _Title;		private string  _MetaKeyword;		private string  _Description;		#endregion		#region[Public Properties]		public string Id{ get { return _Id; } set { _Id = value; } }		public string Name{ get { return _Name; } set { _Name = value; } }		public string Ord{ get { return _Ord; } set { _Ord = value; } }		public string Active{ get { return _Active; } set { _Active = value; } }		public string Tag{ get { return _Tag; } set { _Tag = value; } }		public string Title{ get { return _Title; } set { _Title = value; } }		public string MetaKeyword{ get { return _MetaKeyword; } set { _MetaKeyword = value; } }		public string Description{ get { return _Description; } set { _Description = value; } }		#endregion		#region[NewsCategory IDataReader]		public NewsCategory NewsCategoryIDataReader(IDataReader dr)		{			Data.NewsCategory obj = new Data.NewsCategory();			obj.Id = (dr["Id"] is DBNull) ? string.Empty : dr["Id"].ToString();			obj.Name = (dr["Name"] is DBNull) ? string.Empty : dr["Name"].ToString();			obj.Ord = (dr["Ord"] is DBNull) ? string.Empty : dr["Ord"].ToString();			obj.Active = (dr["Active"] is DBNull) ? string.Empty : dr["Active"].ToString();			obj.Tag = (dr["Tag"] is DBNull) ? string.Empty : dr["Tag"].ToString();			obj.Title = (dr["Title"] is DBNull) ? string.Empty : dr["Title"].ToString();			obj.MetaKeyword = (dr["MetaKeyword"] is DBNull) ? string.Empty : dr["MetaKeyword"].ToString();			obj.Description = (dr["Description"] is DBNull) ? string.Empty : dr["Description"].ToString();			return obj;		}		#endregion	}}