using System;using System.Data;using System.Data.SqlClient;using System.Collections.Generic;namespace MyWeb.Data{	public class tbBilldetail	{		#region[Declare variables]		private string  _id;		private string  _bilid;		private string  _proid;		private string  _sizeid;		private string  _colorid;		private string  _quantity;		private string  _bilprice;		private string  _bilpricevnd;		private string  _bilmoney;		private string  _billlocation;
        private string _ProductName;
        private string _Image;		#endregion		#region[Public Properties]		public string id{ get { return _id; } set { _id = value; } }		public string bilid{ get { return _bilid; } set { _bilid = value; } }		public string proid{ get { return _proid; } set { _proid = value; } }		public string sizeid{ get { return _sizeid; } set { _sizeid = value; } }		public string colorid{ get { return _colorid; } set { _colorid = value; } }		public string quantity{ get { return _quantity; } set { _quantity = value; } }		public string bilprice{ get { return _bilprice; } set { _bilprice = value; } }		public string bilpricevnd{ get { return _bilpricevnd; } set { _bilpricevnd = value; } }		public string bilmoney{ get { return _bilmoney; } set { _bilmoney = value; } }		public string billlocation{ get { return _billlocation; } set { _billlocation = value; } }
        public string ProductName { get { return _ProductName; } set { _ProductName = value; } }
        public string Image { get { return _Image; } set { _Image = value; } }		#endregion		#region[tbBilldetail IDataReader]		public tbBilldetail tbBilldetailIDataReader(IDataReader dr)		{			Data.tbBilldetail obj = new Data.tbBilldetail();			obj.id = (dr["id"] is DBNull) ? string.Empty : dr["id"].ToString();			obj.bilid = (dr["bilid"] is DBNull) ? string.Empty : dr["bilid"].ToString();			obj.proid = (dr["proid"] is DBNull) ? string.Empty : dr["proid"].ToString();			obj.sizeid = (dr["sizeid"] is DBNull) ? string.Empty : dr["sizeid"].ToString();			obj.colorid = (dr["colorid"] is DBNull) ? string.Empty : dr["colorid"].ToString();			obj.quantity = (dr["quantity"] is DBNull) ? string.Empty : dr["quantity"].ToString();			obj.bilprice = (dr["bilprice"] is DBNull) ? string.Empty : dr["bilprice"].ToString();			obj.bilpricevnd = (dr["bilpricevnd"] is DBNull) ? string.Empty : dr["bilpricevnd"].ToString();			obj.bilmoney = (dr["bilmoney"] is DBNull) ? string.Empty : dr["bilmoney"].ToString();			obj.billlocation = (dr["billlocation"] is DBNull) ? string.Empty : dr["billlocation"].ToString();
            obj.ProductName = (dr["ProductName"] is DBNull) ? string.Empty : dr["ProductName"].ToString();
            obj.Image = (dr["Image"] is DBNull) ? string.Empty : dr["Image"].ToString();			return obj;		}		#endregion	}}